import {makeAutoObservable} from "mobx";

export default class VacancyStore {
    constructor() {
        this._employmentTypes = []
        this._locations = []
        this._vacancies = [
            {id: 1 , job_title: "Монтажер для tiktok" , salary: 10000, company: "ООО 'DiDi'",employmentTypeId: 1 , locationId: 1 },
            {id: 2 , job_title: "Монтажер для shorts" , salary: 12000, company: "CPM inc.",employmentTypeId: 2 , locationId: 2 },
            {id: 3 , job_title: "Монтажер для tiktok" , salary: 10000, company: "ООО 'DiDi'",employmentTypeId: 1 , locationId: 1 },
