import React from 'react';
import { Card, Col } from "react-bootstrap";

const VacancyItem = ({ vacancy }) => {
    const fontSize = vacancy.title.length > 20 ? 3 : 4; // Устанавливаем размер шрифта в зависимости от длины заголовка

    return (
        <Col md={3}>
            <Card style={{ width: 150, height: 150, cursor: 'pointer' }} border={"gray"}>
                <div className="d-flex flex-column justify-content-between" style={{ height: "100%" }}>
                    <div className="text-center mt-auto" style={{ fontSize: `${fontSize}vw` }}>{vacancy.title}</div>
                    <div className="text-center mb-auto">12 000</div>
                </div>
            </Card>
        </Col>
    );
};

export default VacancyItem;
