import React, { useEffect, useState } from 'react';
import { Button, Card, Col, Container, Row } from "react-bootstrap";
import { useParams } from 'react-router-dom';
import { fetchOneVacancy } from "../http/vacancyAPI";

const VacancyPage = () => {
    const [vacancy, setVacancy] = useState({}); // Изменено: Используем пустой объект по умолчанию
    const { id } = useParams();

    useEffect(() => {
        // Используем useEffect для загрузки данных о вакансии по ее ID при монтировании компонента
        fetchOneVacancy(id).then(data => setVacancy(data));
    }, [id]); // Изменено: Передаем зависимость id для повторной загрузки данных при изменении ID

    return (
        <Container className="mt-3">
            <Row>
                <Col md={6}>
                    <h2>{vacancy.job_title}</h2>
                    <p>{vacancy.company}</p>
                    <p>{vacancy.salary}</p>
                    {/* Добавьте дополнительную информацию о вакансии, если необходимо */}
                </Col>
                <Col md={6}>
                    <Card style={{ width: '18rem' }}>
                        <Card.Body>
                            <Card.Title>Действия</Card.Title>
                            <Card.Text>
                                <Button variant="primary">Подать заявку</Button>
                                {/* Добавьте другие действия, если необходимо */}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
            <Row className="mt-3">
                <Col>
                    <h3>Описание</h3>
                    <p>{vacancy.description}</p>
                </Col>
            </Row>
            {/* Другие разделы с дополнительной информацией о вакансии, если необходимо */}
        </Container>
    );
};

export default VacancyPage;
