import {makeAutoObservable} from "mobx";

export default class VacancyStore {
    constructor() {
        this._employmentTypes = [
            {id: 1, name: "Полная занятость"},
            {id: 2, name: "Частичная занятость"},
            {id: 3, name: "Плавующий график"},
            {id: 4, name: "Сдельная работа"},
        ]
        this._locations = [
            {id: 1, region: "Владимирская обл.", city: "Владимир"},
            {id: 2, region: "Владимирская обл.", city: "Суздаль"},
            {id: 3, region: "Владимирская обл.", city: "Лакинск"},
            {id: 4, region: "Москва", city: "Подольск"},
        ]
        this._vacancies = [
            {id: 1 , job_title: "Монтажер для tiktok" , salary: 10000, company: "DiDi",employmentTypeId: 1 , locationId: 1 },
            {id: 2 , job_title: "Монтажер для shorts" , salary: 12000, company: "CPM",employmentTypeId: 2 , locationId: 2 },
            {id: 1 , job_title: "Монтажер для tiktok" , salary: 10000, company: "DiDi",employmentTypeId: 1 , locationId: 1 },
            {id: 2 , job_title: "Монтажер для shorts" , salary: 12000, company: "CPM",employmentTypeId: 2 , locationId: 2 }
        ]
        this._selectedLocation = {}
        this._selectedType = {}
        makeAutoObservable(this)
    }

    setEmploymentTypes(employmentTypes){
        this._employmentTypes = employmentTypes
    }
    setLocations(locations){
        this._locations = locations
    }

    setSelectedType(type){
        this._selectedType = type
    }

    setSelectedLocation(location){
        this._selectedLocation = location
    }

    setVacancies(vacancies){
        this._vacancies = vacancies
    }

    get EmploymentTypes(){
        return this._employmentTypes
    }

    get Locations(){
        return this._locations
    }

    get selectedLocation(){
        return this._selectedLocation
    }

    get Vacancies(){
        return this._vacancies
    }

    get selectedType(){
        return this._selectedType
    }

}
