import React, { useState, useEffect } from 'react';
import { Button, Card } from 'react-bootstrap';
import { getAllVacanciesInList, deleteVacancyFromList } from '../http/listApi';

const List = () => {
    const [vacancies, setVacancies] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const data = await getAllVacanciesInList();
                setVacancies(data);
            } catch (error) {
                console.error(error.message);
            }
        };

        fetchData();
    }, []);

    const handleDelete = async (vacancyId) => {
        try {
            await deleteVacancyFromList(vacancyId);
            // Фильтруем список вакансий так, чтобы убрать удаленную вакансию
            setVacancies(prevVacancies => prevVacancies.filter(vacancy => vacancy.id !== vacancyId));
        } catch (error) {
            console.error(error.message);
        }
    };


    return (
        <div>
            <h1>Мой список вакансий</h1>
            {vacancies.map(vacancy => (
                <Card key={vacancy.id}>
                    <Card.Body>
                        <Card.Title>{vacancy.job_title}</Card.Title>
                        <Card.Text>{vacancy.company}</Card.Text>
                        <Button variant="danger" onClick={() => handleDelete(vacancy.id)}>Удалить</Button>
                    </Card.Body>
                </Card>
            ))}
        </div>
    );
};

export default List;
