// В файле VacancyUpdate.js

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Form, Button } from 'react-bootstrap';
import { updateVacancy, fetchOneVacancy } from '../http/vacancyAPI';

const VacancyUpdate = () => {
    const { id } = useParams(); // Получаем id вакансии из URL
    const [formData, setFormData] = useState({
        job_title: '',
        company: '',
        locationId: '',
        salary: 0,
        employmentTypeId: ''
    });

    useEffect(() => {
        // Получаем данные о вакансии при загрузке компонента
        const fetchVacancyData = async () => {
            try {
                const vacancyData = await fetchOneVacancy(id);
                setFormData(vacancyData);
            } catch (error) {
                console.error('Ошибка при загрузке данных о вакансии:', error);
            }
        };
        fetchVacancyData();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateVacancy(id, formData);
            // Обработка успешного обновления вакансии
            console.log('Информация о вакансии успешно обновлена!');
        } catch (error) {
            console.error('Ошибка при обновлении вакансии:', error);
        }
    };

    return (
        <div>
            <h1>Редактирование вакансии</h1>
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formJobTitle">
                    <Form.Label>Название должности</Form.Label>
                    <Form.Control type="text" name="job_title" value={formData.job_title} onChange={handleChange} />
                </Form.Group>
                <Form.Group controlId="formCompany">
                    <Form.Label>Название компании</Form.Label>
                    <Form.Control type="text" name="company" value={formData.company} onChange={handleChange} />
                </Form.Group>
                <Form.Group controlId="formLocation">
                    <Form.Label>Локация</Form.Label>
                    <Form.Control as="select" name="locationId" value={formData.locationId} onChange={handleChange}>
                        {/* Опции для выбора локации */}
                    </Form.Control>
                </Form.Group>
                <Form.Group controlId="formSalary">
                    <Form.Label>Зарплата</Form.Label>
                    <Form.Control type="number" name="salary" value={formData.salary} onChange={handleChange} />
                </Form.Group>
                <Form.Group controlId="formEmploymentType">
                    <Form.Label>Тип занятости</Form.Label>
                    <Form.Control as="select" name="employmentTypeId" value={formData.employmentTypeId} onChange={handleChange}>
                        {/* Опции для выбора типа занятости */}
                    </Form.Control>
                </Form.Group>
                <Button variant="primary" type="submit">
                    Сохранить
                </Button>
            </Form>
        </div>
    );
};

export default VacancyUpdate;
