import React from 'react';
import { Button, Card, Col } from "react-bootstrap";
import { Link } from "react-router-dom";
import { VACANCY_ROUTE } from "../utils/consts";
import { useStores } from "../hooks/useStores"; // Импортируем кастомный хук для доступа к хранилищу

const VacancyItem = ({ vacancy }) => {
    const { vacancyStore } = useStores(); // Получаем доступ к хранилищу

    // Находим объект типа занятости по ID
    const employmentType = vacancyStore.EmploymentTypes.find(type => type.id === vacancy.employmentTypeId);

    const fontSize = vacancy.job_title.length > 20 ? 0.8 : 1;

    return (
        <Col xs={12} className="mb-3">
            <Card
                style={{ cursor: 'pointer', borderLeft: '5px solid lightgreen', height: 200 }}
            >
                <div className="p-3">
                    <div style={{ fontSize: `${fontSize}em` }}>{vacancy.job_title}</div>
                    <div>{vacancy.company}</div>
                    <div>{vacancy.salary} руб.</div>
                    <div>{employmentType ? employmentType.name : 'Unknown Type'}</div> {/* Выводим имя типа занятости */}
                </div>
                <div className="p-3" style={{ marginTop: '-20px' }}>
                    <div className="btn-group" role="group" aria-label="Кнопки">
                        <Link to={VACANCY_ROUTE + `/${vacancy.id}`} style={{ textDecoration: 'none' }}>
                            <Button
                                variant="outline-success"
                                className="mt-3"
                                style={{ fontSize: 14 }}
                            >
                                Просмотреть детально
                            </Button>
                        </Link>
                        <Button
                            variant="outline-secondary"
                            className="mt-3"
                            style={{ fontSize: 14 }}
                        >
                            Добавить в список
                        </Button>
                    </div>
                </div>
            </Card>
        </Col>
    );
};

export default VacancyItem;
