import React, { useContext } from 'react';
import { Context } from "../index";
import { Button, Container, Nav, Navbar } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import {HOME_ROUTE, ADMIN_ROUTE, LOGIN_ROUTE} from "../utils/consts"; // Импортируем маршрут для страницы администратора
import { observer } from "mobx-react-lite";

const NavBar = observer(() => {
    const { user } = useContext(Context);

    return (
        <Navbar bg="dark" data-bs-theme="dark">
            <Container>
                <NavLink style={{ color: "gray" }} to={HOME_ROUTE}>WorkPlace</NavLink>
                {user.isAuth ?
                    <Nav className="ml-auto" style={{ color: "gray" }}>
                        <NavLink to={ADMIN_ROUTE} style={{ textDecoration: 'none' }}> {/* Добавляем NavLink для перехода на страницу администратора */}
                            <Button variant={"outline-light"} className={"m-lg-2"}>Панель Администратора</Button>
                        </NavLink>
                        <NavLink to={LOGIN_ROUTE} style={{ textDecoration: 'none' }}>
                        <Button variant={"outline-light"} className={"m-lg-2"} onClick={() => user.setIsAuth(false)}>Выйти</Button>
                        </NavLink>
                    </Nav>
                    :
                    <Nav className="ml-auto" style={{ color: "gray" }}>
                        <Button variant={"outline-light"} className={"m-lg-2"} onClick={() => user.setIsAuth(true)}>Авторизация</Button>
                    </Nav>
                }
            </Container>
        </Navbar>
    );
});

export default NavBar;
