import React, { useContext, useState } from 'react';
import { Button, Card, Container, Form, Row } from "react-bootstrap";
import { NavLink, useHistory, useLocation } from "react-router-dom"; // Импортируем useHistory
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from "../utils/consts";
import { login, registration } from "../http/userAPI";
import { observer } from "mobx-react-lite";
import { Context } from "../index";

const Auth = observer(() => {
    const history = useHistory(); // Инициализируем history
    const { user } = useContext(Context);
    const location = useLocation();
    const isLogin = location.pathname === LOGIN_ROUTE;
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('worker');

    const handleRoleChange = (e) => {
        setRole(e.target.value);
    }

    const click = async () => {
        try {
            let data;
            if (isLogin) {
                data = await login(email, password);
            } else {
                let roleToSend = '';
                if (role === 'worker') {
                    roleToSend = 'USER';
                } else if (role === 'employer') {
                    roleToSend = 'EMPLOYER';
                }
                data = await registration(email, password, roleToSend);
            }
            user.setUser(user);
            user.setIsAuth(true);
            history.push('/home'); // Перенаправляем пользователя на /home после успешной регистрации/авторизации
        } catch (e) {
            alert(e.response.data.message);
        }
    }

    return (
        <Container
            className="d-flex justify-content-center align-items-center"
            style={{ height: window.innerHeight - 54 }}
        >
            <Card style={{ width: 600 }} className="p-5">
                <h2 className="m-auto">{isLogin ? "Авторизация" : "Регистрация"}</h2>
                <Form className="d-flex flex-column">
                    <Form.Control
                        className="mt-3"
                        placeholder="E-mail"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                    <Form.Control
                        className="mt-3"
                        placeholder="Password"
                        type="password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                    {!isLogin && (
                        <Form.Group controlId="role">
                            <Form.Check
                                type="radio"
                                label="Я работник"
                                value="worker"
                                checked={role === 'worker'}
                                onChange={handleRoleChange}
                            />
                            <Form.Check
                                type="radio"
                                label="Я наниматель"
                                value="employer"
                                checked={role === 'employer'}
                                onChange={handleRoleChange}
                            />
                        </Form.Group>
                    )}
                    <Row className="d-flex justify-content-between mt-3 pl-3 pr-3 align-items-center">
                        {isLogin ?
                            <div>
                                Нет аккаунта? <NavLink to={REGISTRATION_ROUTE}>Зарегистрируйтесь</NavLink>
                            </div>
                            :
                            <div>
                                Есть аккаунт? <NavLink to={LOGIN_ROUTE}>Авторизируйтесь</NavLink>
                            </div>
                        }
                        <Button
                            className="mt-3 align-self-end"
                            variant="outline-success"
                            onClick={click}
                        >
                            {isLogin ? "Войти" : "Зарегистрироваться"}
                        </Button>
                    </Row>
                </Form>
            </Card>
        </Container>
    );
});

export default Auth;
