import React from 'react';
import {Button, Card, Col} from "react-bootstrap";

const VacancyItem = ({ vacancy }) => {
    const fontSize = vacancy.job_title.length > 20 ? 0.8 : 1; // Устанавливаем размер шрифта в зависимости от длины заголовка

    return (
        <Col xs={12} md={3} className="mb-3"> {/* Установка ширины для колонок Bootstrap */}
            <Card style={{ cursor: 'pointer' }} border={"gray"}>
                <div className="d-flex flex-column justify-content-between align-items-center" style={{ height: "100%", width: "100%" }}>
                    <div className="text-center mt-0" style={{ fontSize: `${fontSize}em` }}>{vacancy.job_title}</div>
                    <Button
                        variant="outline-success"
                        style={{ width: "100%", fontSize: 14 }} // Ширина кнопки равна ширине карточки
                    >
                        Откликнуться
                    </Button>
                    <div className="text-center mb-0">12 000</div>
                </div>
            </Card>
        </Col>
    );
};

export default VacancyItem;
