import React from 'react';
import { useParams } from 'react-router-dom';
import { Button, Card, Col, Container, Row } from 'react-bootstrap';

const VacancyPage = () => {
    const { id } = useParams(); // Получаем параметр id из URL

    // Примерные данные о вакансии для демонстрации
    const vacancy = {
        id: id,
        job_title: 'Frontend Developer',
        company: 'Tech Company',
        salary: '$5000 - $7000 per month',
    };

    // Примерные данные о дополнительной информации о вакансии
    const vacancyInfo = [
        { id: 1, vacancyId: id, title: 'Требования', description: 'Опыт работы от 2 лет, знание JavaScript, HTML, CSS.' },
        { id: 2, vacancyId: id, title: 'Условия', description: 'Гибкий график, интересные проекты, обучение и карьерный рост.' },
        { id: 3, vacancyId: id, title: 'Контакты', description: 'Телефон: +7 (123) 456-78-90, email: example@example.com' },
    ];

    return (
        <Container className="mt-3">
            <Row>
                <Col md={6}>
                    <h2>{vacancy.job_title}</h2>
                    <p>{vacancy.company}</p>
                    <p>{vacancy.salary}</p>
                </Col>
                <Col md={6}>
                    <Card style={{ width: '20rem' }}>
                        <Card.Body>
                            <Card.Title>Понравилось предложение? Добавьте в список!</Card.Title>
                            <Card.Text>
                                <Button variant="primary">Добавить</Button>
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
            <Row className="mt-3">
                <Col>
                    <h3>Дополнительная информация</h3>
                    {vacancyInfo.map(info => (
                        <div key={info.id}>
                            <h4>{info.title}</h4>
                            <p>{info.description}</p>
                        </div>
                    ))}
                </Col>
            </Row>
        </Container>
    );
};

export default VacancyPage;
