import React, { useState, useEffect } from 'react';
import { Button, Card } from 'react-bootstrap';
import axios from 'axios';

const List = () => {
    const [vacancies, setVacancies] = useState([]);

    // Функция для загрузки списка вакансий пользователя
    const loadVacancies = async () => {
        try {
            const response = await axios.get('/api/list');
            setVacancies(response.data);
        } catch (error) {
            console.error('Ошибка при загрузке списка вакансий:', error);
        }
    };

    // Загрузка списка вакансий при загрузке компонента
    useEffect(() => {
        loadVacancies();
    }, []);

    // Функция для удаления вакансии из списка
    const deleteVacancy = async (vacancyId) => {
        try {
            await axios.delete('/api/list', { data: { vacancyId } });
            // После удаления вакансии обновляем список
            loadVacancies();
        } catch (error) {
            console.error('Ошибка при удалении вакансии из списка:', error);
        }
    };

    return (
        <div>
            <h1>Мой список вакансий</h1>
            {vacancies.map(vacancy => (
                <Card key={vacancy.id}>
                    <Card.Body>
                        <Card.Title>{vacancy.job_title}</Card.Title>
                        <Card.Text>{vacancy.company}</Card.Text>
                        <Button variant="danger" onClick={() => deleteVacancy(vacancy.id)}>Удалить</Button>
                    </Card.Body>
                </Card>
            ))}
        </div>
    );
};

export default List;
