import React, { useContext, useEffect, useState } from 'react';
import { Button, Col, Dropdown, Form, Modal, Row } from "react-bootstrap";
import { Context } from "../../index";
import { updateVacancy, fetchLocation, fetchTypes, fetchOneVacancy } from "../../http/vacancyAPI";
import { observer } from "mobx-react-lite";

const UpdateVacancy = observer(({ show, onHide, vacancyId }) => {
    const { vacancy } = useContext(Context);
    const [info, setInfo] = useState([]);
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [salary, setSalary] = useState(0);

    const fetchData = async () => {
        try {
            const vacancyData = await fetchOneVacancy(vacancyId);
            setJobTitle(vacancyData.job_title);
            setCompany(vacancyData.company);
            setSalary(vacancyData.salary);
            // Здесь можно подгрузить другие поля из базы данных для редактирования
        } catch (error) {
            console.error('Ошибка при получении информации о вакансии:', error);
        }
    };

    const addInfo = () => {
        setInfo([...info, { title: '', description: '', number: Date.now() }]);
    };

    const deleteInfo = (number) => {
        setInfo(info.filter(item => item.number !== number));
    };

    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? { ...i, [key]: value } : i));
    };

    const editVacancy = async () => {
        try {
            const vacancyData = {
                job_title: jobTitle,
                company: company,
                salary: salary,
                // Добавьте остальные поля вакансии для обновления
            };

            await updateVacancy(vacancyId, vacancyData);
            onHide();
        } catch (error) {
            console.error('Ошибка при обновлении вакансии:', error);
        }
    };

    useEffect(() => {
        fetchData();
        fetchTypes().then(data => vacancy.setEmploymentTypes(data));
        fetchLocation().then(data => vacancy.setLocations(data));
    }, []);

    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title>
                    Редактирование вакансии
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        value={jobTitle}
                        onChange={e => setJobTitle(e.target.value)}
                        placeholder="Название должности"
                        className={"mt-2 mb-2"}
                    />
                    <Form.Control
                        value={company}
                        onChange={e => setCompany(e.target.value)}
                        placeholder="Название компании"
                        className={"mt-2 mb-2"}
                    />
                    <Form.Control
                        value={salary}
                        onChange={e => setSalary(Number(e.target.value))}
                        placeholder="Зарплата в рублях"
                        className={"mt-2 mb-2"}
                        type={"number"}
                    />
                    {/* Добавьте остальные поля для редактирования */}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide} >Закрыть</Button>
                <Button variant="outline-success" onClick={editVacancy}>Сохранить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default UpdateVacancy;
