import React from 'react';
import {Button, Card, Col} from "react-bootstrap";

const VacancyItem = ({ vacancy }) => {
    const fontSize = vacancy.job_title.length > 20 ? 0.8 : 1; // Устанавливаем размер шрифта в зависимости от длины заголовка

    return (
        <Col xs={12} className="mb-3">
            <Card
                style={{ cursor: 'pointer', borderLeft: '4px solid orange' }}
                border={"white"}
                onMouseEnter={(e) => e.target.style.border = "1px solid gray"}
                onMouseLeave={(e) => e.target.style.border = "1px solid white"}
            >
                <div className="d-flex flex-column justify-content-between align-items-start" style={{ height: "100%", width: "100%" }}>
                    <div className="p-3" style={{ fontSize: `${fontSize}em` }}>{vacancy.job_title}</div>
                    <div className="p-3">12 000</div>
                    <Button
                        variant="outline-success"
                        className="align-self-start ml-3 mb-3"
                        style={{ fontSize: 14 }}
                    >
                        Откликнуться
                    </Button>
                </div>
            </Card>
        </Col>
    );
};

export default VacancyItem;
