import React, {useContext, useState} from 'react';
import {Button, Col, Dropdown, Form, Modal, Row} from "react-bootstrap";
import employmentTypeBar from "../EmploymentTypeBar";
import {Context} from "../../index";

const CreateVacancy = ({show, onHide}) => {
    const {vacancy} = useContext(Context)
    const [info, setInfo] = useState([])
    const [jobTitle, setJobTitle] = useState('');
    const [company, setCompany] = useState('');
    const [salary, setSalary] = useState(0);
    const [region, setRegion] = useState('');
    const [city, setCity] = useState('');
    const [type, setType] = useState('');
    const addInfo = () => {
        setInfo([...info, {title: '', description: '', number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }


    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title>
                    Создание вакансии
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        value={jobTitle}
                        placeholder="Название должности"
                        className={"mt-2 mb-2"}
                    />
                    <Form.Control
                        value={company}
                        placeholder="Название компании"
                        className={"mt-2 mb-2"}
                    />
                    <Form.Control
                        value={salary}
                        placeholder="Зарплата в рублях"
                        className={"mt-2 mb-2"}
                        type={"number"}
                    />
                    <Dropdown className={"mt-2 mb-2"}>
                        <Dropdown.Toggle>Выберите тип</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {vacancy.EmploymentTypes.map(employmentType =>
                                <Dropdown.Item key={employmentType.id}>{employmentType.name} </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Dropdown className={"mt-2 mb-2"}>
                        <Dropdown.Toggle>Выберите локацию</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {vacancy.Locations.map(location =>
                                <Dropdown.Item key={location.id}>{location.region} {location.city} </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <hr/>
                    <Button
                        variant={"outline-dark"}
                        onClick={addInfo}
                    >
                        Добавить новое свойство
                    </Button>
                    {info.map(i =>
                        <Row className={"mt-3"} key={i.number}>
                            <Col md={4}>
                                <Form.Control
                                    placeholder={"Название свойства"}
                                />
                            </Col>
                            <Col md={4}>
                                <Form.Control
                                    placeholder={"Описание свойства"}
                                />
                            </Col>
                            <Col md={4}>
                                <Button
                                    variant={"outline-danger"}
                                    onClick={() => removeInfo(i.number)}
                                >
                                    Удалить
                                </Button>
                            </Col>
                        </Row>
                    )}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success">Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateVacancy;
