import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { Form, Button, Dropdown } from 'react-bootstrap';
import { updateVacancy, fetchOneVacancy, fetchLocation, fetchTypes } from '../http/vacancyAPI';
import { Context } from "../index";

const VacancyUpdate = () => {
    const { id } = useParams(); // Получаем id вакансии из URL
    const { vacancy } = useContext(Context);
    const [formData, setFormData] = useState({
        job_title: '',
        company: '',
        locationId: '',
        salary: 0,
        employmentTypeId: ''
    });
    const [locations, setLocations] = useState([]);
    const [types, setTypes] = useState([]);

    useEffect(() => {
        fetchTypes().then(data => vacancy.setEmploymentTypes(data));
        fetchLocation().then(data => vacancy.setLocations(data));
    }, []);

    useEffect(() => {
        // Получаем данные о вакансии при загрузке компонента
        const fetchVacancyData = async () => {
            try {
                const vacancyData = await fetchOneVacancy(id);
                setFormData(vacancyData);
            } catch (error) {
                console.error('Ошибка при загрузке данных о вакансии:', error);
            }
        };
        fetchVacancyData();
        // Получаем список локаций и типов занятости при загрузке компонента
        fetchLocation().then(data => setLocations(data));
        fetchTypes().then(data => setTypes(data));
    }, [id]);

    const handleLocationChange = (location) => {
        setFormData({ ...formData, locationId: location.id });
    };

    const handleTypeChange = (type) => {
        setFormData({ ...formData, employmentTypeId: type.id });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateVacancy(id, formData);
            // Обработка успешного обновления вакансии
            console.log('Информация о вакансии успешно обновлена!');
        } catch (error) {
            console.error('Ошибка при обновлении вакансии:', error);
        }
    };

    return (
        <div>
            <h1>Редактирование вакансии</h1>
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="formJobTitle">
                    <Form.Label>Название должности</Form.Label>
                    <Form.Control type="text" name="job_title" value={formData.job_title} onChange={handleChange} />
                </Form.Group>
                <Form.Group controlId="formCompany">
                    <Form.Label>Название компании</Form.Label>
                    <Form.Control type="text" name="company" value={formData.company} onChange={handleChange} />
                </Form.Group>
                <Form.Group controlId="formLocation">
                    <Form.Label>Локация</Form.Label>
                    <Dropdown className={"mt-2 mb-2"}>
                        <Dropdown.Toggle>{vacancy.selectedLocation.city ? (vacancy.selectedLocation.city + " " + vacancy.selectedLocation.region) : "Выберите локацию"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {vacancy.Locations.map(location =>
                                <Dropdown.Item onClick={() => handleLocationChange(location)} key={location.id}>{location.region} {location.city} </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                </Form.Group>
                <Form.Group controlId="formSalary">
                    <Form.Label>Зарплата</Form.Label>
                    <Form.Control type="number" name="salary" value={formData.salary} onChange={handleChange} />
                </Form.Group>
                <Form.Group controlId="formEmploymentType">
                    <Form.Label>Тип занятости</Form.Label>
                    <Dropdown className={"mt-2 mb-2"}>
                        <Dropdown.Toggle>{vacancy.selectedType.name || "Выберите тип"}</Dropdown.Toggle>
                        <Dropdown.Menu>
                            {vacancy.EmploymentTypes.map(employmentType =>
                                <Dropdown.Item onClick={() => handleTypeChange(employmentType)} key={employmentType.id}>{employmentType.name} </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                </Form.Group>
                <Button variant="primary" type="submit">
                    Сохранить
                </Button>
            </Form>
        </div>
    );
};

export default VacancyUpdate;
