import React from 'react';
import { Button, Card, Col } from "react-bootstrap";

const VacancyItem = ({ vacancy }) => {
    const fontSize = vacancy.job_title.length > 20 ? 0.8 : 1; // Устанавливаем размер шрифта в зависимости от длины заголовка

    return (
        <Col xs={12} className="mb-3">
            <Card
                style={{ cursor: 'pointer', borderLeft: '5px solid lightgreen', height: 200 }} // Задаем зеленую границу слева шириной в 5 пикселей
            >
                <div className="p-3">
                    <div style={{ fontSize: `${fontSize}em` }}>{vacancy.job_title}</div>
                    <div>{vacancy.company}</div>
                    <div>{vacancy.salary}</div>
                </div>
                <div className="p-3 d-flex justify-content-between align-items-center"> {/* Добавляем классы d-flex и justify-content-between */}
                    <div>
                        <Button
                            variant="outline-success"
                            className="mt-3"
                            style={{ fontSize: 14 }}
                        >
                            Откликнуться
                        </Button>
                    </div>
                    <div>
                        <Button
                            variant="outline-secondary" // Изменяем вариант кнопки на вторичный
                            className="mt-3"
                            style={{ fontSize: 14 }}
                        >
                            Добавить в список
                        </Button>
                    </div>
                </div>
            </Card>
        </Col>
    );
};

export default VacancyItem;
